import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, useParams } from 'react-router-dom';
import './LoginPage.css'; // Use the same CSS file for consistent styling

function ForgotPasswordPage() {
  const { token } = useParams(); // Get the token from the URL
  const [newPassword, setNewPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleSetNewPassword = () => {
    axios.post(`http://localhost:3001/reset_password/${token}`, { newPassword })
      .then(() => {
        setMessage('Password updated successfully.');
        setTimeout(() => navigate('/login'), 2000); // Redirect after a short delay
      })
      .catch(() => setMessage('Error updating password.'));
  };

  return (
    <div className="auth-container">
      <h2>Set New Password</h2>
      <div className="auth-form">
        <input
          type="password"
          placeholder="Enter new password"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
          className="auth-input"
        />
        <button onClick={handleSetNewPassword} className="auth-button">Set New Password</button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    </div>
  );
}

export default ForgotPasswordPage;
