import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './HomePage.css'; // Importing the CSS file for styling

function HomePage() {
  const [notes, setNotes] = useState([]);
  const [message, setMessage] = useState('');
  const [token] = useState(localStorage.getItem('token'));
  const navigate = useNavigate();

  const handleGetNotes = () => {
    if (token) {
      axios.get('http://localhost:3002/notes', {
        headers: { Authorization: `Bearer ${token}` }
      })
        .then(res => setNotes(res.data))
        .catch(() => setMessage('Failed to retrieve notes.'));
    } else {
      setMessage('Please login first.');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login'); // Redirect to login page after logout
  };

  return (
    <div className="home-container">
      <h2>Notes</h2>
      <button onClick={handleGetNotes} className="home-button">Get Notes</button>
      <button onClick={handleLogout} className="home-button">Logout</button>
      {message && <p className="home-message">{message}</p>}
      <ul className="notes-list">
        {notes.map(note => (
          <li key={note.id} className="note-item">
            <h3>{note.title}</h3>
            <p>{note.content}</p>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default HomePage;
