import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [token, setToken] = useState(null);
  const [products, setProducts] = useState([]);
  const [message, setMessage] = useState('');

  const handleRegister = () => {
    axios.post('http://localhost:3001/register', { username, password })
      .then(() => setMessage('User registered successfully!'))
      .catch(err => {
        if (err.response && err.response.status === 400) {
          setMessage('User already exists. Please choose another username.');
        } else {
          setMessage('Registration failed. Please try again.');
        }
      });
  };

  const handleLogin = () => {
    axios.post('http://localhost:3001/login', { username, password })
      .then(res => {
        setToken(res.data.token);
        setMessage('Login successful!');
      })
      .catch(() => setMessage('Login failed. Please check your credentials.'));
  };

  const handleGetProducts = () => {
    if (token) {
      axios.get('http://localhost:3002/products', {
        headers: { Authorization: `Bearer ${token}` }
      })
      .then(res => setProducts(res.data))
      .catch(() => setMessage('Failed to retrieve products.'));
    } else {
      setMessage('Please login first.');
    }
  };

  return (
    <div style={{ padding: '20px' }}>
      <h2>Product Application</h2>
      <div style={{ marginBottom: '10px' }}>
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          style={{ marginRight: '10px' }}
        />
        <button onClick={handleRegister} style={{ marginRight: '10px' }}>Register</button>
        <button onClick={handleLogin} style={{ marginRight: '10px' }}>Login</button>
        <button onClick={handleGetProducts}>Get Products</button>
      </div>
      <div>
        {message && <p style={{ color: 'red' }}>{message}</p>}
        <ul>
          {products.map(product => (
            <li key={product.id}>{product.name} - ${product.price}</li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
