import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // Import the useNavigate hook
import axios from 'axios';
import './HomePage.css'; // Import your updated CSS file

const HomePage = () => {
    const [mood, setMood] = useState('');
    const [quote, setQuote] = useState('');
    const [songs, setSongs] = useState([]);
    const [imageUrl, setImageUrl] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate(); // Initialize the navigate function

    // Function to fetch mood info with JWT token
    const fetchMoodInfo = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('User not authenticated. Please log in.');
                return;
            }

            const response = await axios.post(
                'http://localhost:3002/mood',
                { mood },
                {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                }
            );

            if (response.data) {
                setQuote(response.data.quote);
                setSongs(response.data.songs);
                setImageUrl(response.data.image_url);
                setError('');
            } else {
                setError('Unexpected response format.');
            }
        } catch (err) {
            if (err.response && err.response.status === 401) {
                setError('Unauthorized. Please log in again.');
            } else {
                setError('Error fetching mood information.');
            }
        }
    };

    // Function to handle dropdown change
    const handleMoodChange = (e) => {
        setMood(e.target.value);
    };

    // Function to handle logout and navigate to the login page
    const handleLogout = () => {
        localStorage.removeItem('token');  // Remove the JWT token
        navigate('/login');  // Redirect to login page
    };

    return (
        <div className="home-container">
            {/* Logout button */}
            <button className="logout-button" onClick={handleLogout}>
                Logout
            </button>

            <h1>What's Your Mood?</h1>

            {/* Dropdown for mood selection */}
            <select value={mood} onChange={handleMoodChange} className="home-select">
                <option value="">Select your mood...</option>
                <option value="happy">Happy</option>
                <option value="sad">Sad</option>
                <option value="energized">Energized</option>
                <option value="sleepy">Sleepy</option>
                <option value="moody">Moody</option>
            </select>

            <button className="home-button" onClick={fetchMoodInfo}>
                Get Mood Info
            </button>

            {error && <div className="home-message">{error}</div>}

            {quote && <div className="home-quote">{quote}</div>}

            {imageUrl && <img src={imageUrl} alt="Mood" className="home-image" />}

            {songs.length > 0 && (
                <>
                    <div className="songs-header">Here are some songs that will help you make it through the day</div>
                    <table className="songs-table">
                        <thead>
                            <tr>
                                <th>Song Title</th>
                                <th>Listen</th>
                            </tr>
                        </thead>
                        <tbody>
                            {songs.map((song, index) => (
                                <tr key={index} className="song-item">
                                    <td>{song.title}</td>
                                    <td>
                                        <a href={song.url} target="_blank" rel="noopener noreferrer">
                                            Play
                                        </a>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </>
            )}
        </div>
    );
};

export default HomePage;
