import React, { useState } from 'react';
import axios from 'axios';
import './HomePage.css';

const HomePage = () => {
    const [mood, setMood] = useState('');
    const [quote, setQuote] = useState('');
    const [songs, setSongs] = useState([]);
    const [imageUrl, setImageUrl] = useState('');
    const [error, setError] = useState('');
    const [showSongs, setShowSongs] = useState(false); // New state to control song visibility

    const fetchMoodInfo = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('User not authenticated. Please log in.');
                return;
            }

            const response = await axios.post(
                'http://localhost:3002/mood',
                { mood },
                {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                }
            );

            if (response.data) {
                setQuote(response.data.quote);
                setSongs(response.data.songs);
                setImageUrl(response.data.image_url);
                setShowSongs(true);  // Show the songs section only when mood info is fetched
                setError('');
            } else {
                setError('Unexpected response format.');
            }
        } catch (err) {
            if (err.response && err.response.status === 401) {
                setError('Unauthorized. Please log in again.');
            } else {
                setError('Error fetching mood information.');
            }
        }
    };

    const handleMoodChange = (e) => {
        setMood(e.target.value);
        setShowSongs(false);  // Reset the songs section when changing mood
    };

    return (
        <div className="home-container">
            <button className="logout-button" onClick={() => { localStorage.removeItem('token'); window.location.href = '/login'; }}>
                Logout
            </button>

            <h1>What's Your Mood?</h1>

            <select value={mood} onChange={handleMoodChange} className="home-select">
                <option value="">Select your mood...</option>
                <option value="happy">Happy</option>
                <option value="sad">Sad</option>
                <option value="energized">Energized</option>
                <option value="sleepy">Sleepy</option>
                <option value="moody">Moody</option>
            </select>

            <button className="home-button" onClick={fetchMoodInfo}>
                Get Mood Info
            </button>

            {error && <div className="home-message">{error}</div>}

            <div className="content-container">
                {quote && <div className="home-quote"><img src={imageUrl} alt="Mood" className="home-image" />{quote}</div>}

                {showSongs && (
                    <>
                        <h3 className="announcement">Here are some songs that will help you make it through the day</h3>
                        <table className="songs-table">
                            <thead>
                                <tr>
                                    <th>Song Title</th>
                                    <th>Listen</th>
                                </tr>
                            </thead>
                            <tbody>
                                {songs.map((song, index) => (
                                    <tr key={index}>
                                        <td>{song.title}</td>
                                        <td>
                                            <a href={song.url} target="_blank" rel="noopener noreferrer">
                                                Play
                                            </a>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </>
                )}
            </div>
        </div>
    );
};

export default HomePage;
