import React, { useState } from 'react';

function MoodPage() {
  const [mood, setMood] = useState('');
  const [quote, setQuote] = useState('');
  const [songs, setSongs] = useState([]);
  const [imageUrl, setImageUrl] = useState('');
  const [error, setError] = useState('');

  const handleMoodChange = (event) => {
    setMood(event.target.value);
  };

  const fetchMoodData = async () => {
    const token = localStorage.getItem('token'); // Get JWT from storage
    const requestOptions = {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`,  // Include JWT
      },
      body: JSON.stringify({ mood: mood }),
    };

    try {
      const response = await fetch('http://localhost:3002/mood', requestOptions);
      if (response.ok) {
        const data = await response.json();
        setQuote(data.quote);
        setSongs(data.songs);
        setImageUrl(data.image_url);
      } else {
        setError('Failed to retrieve mood information.');
      }
    } catch (error) {
      setError('Error occurred while fetching mood data.');
    }
  };

  return (
    <div>
      <h1>How are you feeling today?</h1>
      <select value={mood} onChange={handleMoodChange}>
        <option value="">Select Mood</option>
        <option value="happy">Happy</option>
        <option value="sad">Sad</option>
        <option value="excited">Excited</option>
        <option value="calm">Calm</option>
        {/* Add more mood options as needed */}
      </select>
      <button onClick={fetchMoodData}>Submit</button>

      {quote && <div><h2>Quote:</h2><p>{quote}</p></div>}

      {songs.length > 0 && (
        <div>
          <h2>Songs:</h2>
          <ul>
            {songs.map((song, index) => (
              <li key={index}>
                <a href={song.url} target="_blank" rel="noopener noreferrer">{song.title}</a>
              </li>
            ))}
          </ul>
        </div>
      )}

      {imageUrl && <div><img src={imageUrl} alt={mood} style={{ width: '200px', height: '200px' }} /></div>}

      {error && <p style={{ color: 'red' }}>{error}</p>}
    </div>
  );
}

export default MoodPage;
