import React, { useState } from 'react';
import axios from 'axios';
import './HomePage.css'; // Import your CSS file

const HomePage = () => {
    const [mood, setMood] = useState('');
    const [quote, setQuote] = useState('');
    const [songs, setSongs] = useState([]);
    const [imageUrl, setImageUrl] = useState('');
    const [error, setError] = useState('');

    const fetchMoodInfo = async () => {
        try {
            const token = localStorage.getItem('token');  // Assuming the JWT token is stored in localStorage after login
            const response = await axios.post(
                'http://localhost:3002/mood',
                { mood },
                {
                    headers: {
                        Authorization: `Bearer ${token}`  // Pass the JWT token in the Authorization header
                    }
                }
            );
            setQuote(response.data.quote);
            setSongs(response.data.songs);
            setImageUrl(response.data.image_url);
            setError('');
        } catch (err) {
            setError('Error fetching mood information.');
        }
    };


    return (
        <div className="home-container">
            <h1>What's Your Mood?</h1>
            <input
                type="text"
                placeholder="Enter your mood..."
                value={mood}
                onChange={(e) => setMood(e.target.value)}
            />
            <button className="home-button" onClick={fetchMoodInfo}>
                Get Mood Info
            </button>
            {error && <div className="home-message">{error}</div>}
            {quote && <div className="home-quote">{quote}</div>}
            {imageUrl && <img src={imageUrl} alt="Mood" className="home-image" />}
            {songs.length > 0 && (
                <ul className="songs-list">
                    {songs.map((song, index) => (
                        <li key={index} className="song-item">
                            <a href={song.url} target="_blank" rel="noopener noreferrer">
                                {song.title}
                            </a>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default HomePage;
