import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './RegisterPage.css'; // Importing the CSS file for styling

function RegisterPage() {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleRegister = () => {
    axios.post('http://localhost:3001/register', { username, password })
      .then(() => {
        setMessage('Registration successful! You can now log in.');
        navigate('/login'); // Redirect to login page after successful registration
      })
      .catch(() => setMessage('Registration failed. User might already exist.'));
  };

  return (
    <div className="auth-container">
      <h2>Register</h2>
      <div className="auth-form">
        <input
          type="text"
          placeholder="Username"
          value={username}
          onChange={(e) => setUsername(e.target.value)}
          className="auth-input"
        />
        <input
          type="password"
          placeholder="Password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          className="auth-input"
        />
        <button onClick={handleRegister} className="auth-button">Register</button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    </div>
  );
}

export default RegisterPage;
