import React, { useState } from 'react';
import axios from 'axios';
import './HomePage.css';

const HomePage = () => {
    const [mood, setMood] = useState('');
    const [quote, setQuote] = useState('');
    const [songs, setSongs] = useState([]);
    const [imageUrl, setImageUrl] = useState('');
    const [error, setError] = useState('');

    // Function to fetch mood info with JWT token
    const fetchMoodInfo = async () => {
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('User not authenticated. Please log in.');
                return;
            }

            const response = await axios.post(
                'http://localhost:3002/mood',
                { mood },
                {
                    headers: {
                        Authorization: `Bearer ${token}`
                    }
                }
            );

            if (response.data) {
                setQuote(response.data.quote);
                setSongs(response.data.songs);
                setImageUrl(response.data.image_url);
                setError('');
            } else {
                setError('Unexpected response format.');
            }
        } catch (err) {
            if (err.response && err.response.status === 401) {
                setError('Unauthorized. Please log in again.');
            } else {
                setError('Error fetching mood information.');
            }
        }
    };

    return (
        <div className="home-container">
            <h1>What's Your Mood?</h1>

            <select value={mood} onChange={(e) => setMood(e.target.value)} className="home-select">
                <option value="">Select your mood...</option>
                <option value="happy">Happy</option>
                <option value="sad">Sad</option>
                <option value="energized">Energized</option>
                <option value="sleepy">Sleepy</option>
                <option value="moody">Moody</option>
            </select>

            <button className="home-button" onClick={fetchMoodInfo}>
                Get Mood Info
            </button>

            {error && <div className="home-message">{error}</div>}

            {/* Display the quote and image on the left side */}
            <div style={{ display: 'flex', flexDirection: 'row' }}>
                {quote && <div className="home-quote">{quote}</div>}
                {imageUrl && <img src={imageUrl} alt="Mood" className="home-image" />}
            </div>

            {/* Display announcement and song table after mood is selected */}
            {songs.length > 0 && (
                <>
                    <div className="songs-announcement">Here are some songs that will help you make it through the day</div>
                    <table className="songs-table">
                        <thead>
                            <tr>
                                <th>Song Title</th>
                                <th>Listen</th>
                            </tr>
                        </thead>
                        <tbody>
                            {songs.map((song, index) => (
                                <tr key={index}>
                                    <td>{song.title}</td>
                                    <td>
                                        <a href={song.url} target="_blank" rel="noopener noreferrer">Play</a>
                                    </td>
                                </tr>
                            ))}
                        </tbody>
                    </table>
                </>
            )}
        </div>
    );
};

export default HomePage;
