import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import './LoginPage'; // Create this CSS file for styling

function ForgotPasswordPage() {
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleForgotPassword = () => {
    axios.post('http://localhost:3001/forgot_password', { email })
      .then(() => {
        setMessage('Password reset link sent to your email.');
        setTimeout(() => navigate('/login'), 2000); // Redirect after a short delay
      })
      .catch(() => setMessage('Error sending password reset link.'));
  };

  return (
    <div className="auth-container">
      <h2>Forgot Password</h2>
      <div className="auth-form">
        <input
          type="email"
          placeholder="Enter your email"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          className="auth-input"
        />
        <button onClick={handleForgotPassword} className="auth-button">Send Reset Link</button>
        {message && <p className="auth-message">{message}</p>}
      </div>
    </div>
  );
}

export default ForgotPasswordPage;
